import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [documents, setDocuments] = useState([]);
  const [newDocument, setNewDocument] = useState({ name: '', priority: false });

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/documents');
      const data = await response.json();
      // Sort documents: priority first, then by name
      const sortedData = data.sort((a, b) => {
        if (a.priority === b.priority) {
          return a.name.localeCompare(b.name);
        }
        return b.priority ? 1 : -1;
      });
      setDocuments(sortedData);
    } catch (error) {
      console.error('Error fetching documents:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8080/api/documents', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newDocument),
      });
      if (response.ok) {
        setNewDocument({ name: '', priority: false });
        fetchDocuments();
      }
    } catch (error) {
      console.error('Error adding document:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Print Management System</h1>
      </header>
      
      <div className="add-document-form">
        <h2>Add New Document</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Document Name"
            value={newDocument.name}
            onChange={(e) => setNewDocument({ ...newDocument, name: e.target.value })}
            required
          />
          <label className="priority-label">
            <input
              type="checkbox"
              checked={newDocument.priority}
              onChange={(e) => setNewDocument({ ...newDocument, priority: e.target.checked })}
            />
            Priority Document
          </label>
          <button type="submit">Add Document</button>
        </form>
      </div>

      <div className="documents-list">
        <h2>Print Queue</h2>
        <div className="queue-info">
          <p>Priority documents will be printed first</p>
        </div>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Priority</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {documents.map((doc) => (
              <tr key={doc.id} className={doc.priority ? 'priority-row' : ''}>
                <td>
                  {doc.name}
                  {doc.priority && <span className="priority-badge">Priority</span>}
                </td>
                <td>{doc.priority ? 'Yes' : 'No'}</td>
                <td>{doc.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
